import React, { useState, useEffect } from 'react'
import axios from 'axios'

const dbug = true
const dataHost = 'https://restcountries.com/v3.1/all'

const Filter = (props) => {
  if (dbug) console.log('renderöidään filtteri...')

  return (
    <>
    find countries <input
      value={props.filterPhrase}
      onChange={props.handleFilterChange}
      />
    </>
  )
}

const RenderCountries = ({ countries, filterPhrase }) => {
  if (dbug) console.log('renderöidään maa(t)...')

  const fltrdCntrs = countries.filter(country =>
                        country.name.common.toLowerCase()
                          .includes(filterPhrase.toLowerCase))

  return (
    <div>
      <RenderCountry country={fltrdCntrs[0]} />
    </div>
  ) 
}

const RenderCountry = ({ country }) => {
  return (
    <>
    <h1>{country.name.common}</h1>
    <p>
      capital {country.capital[0]}
      <br/>
      population {country.population}
    </p>
    <h2>languages</h2>
    </>
  )
}

const App = () => {
  const [countries, setCountries] = useState([{name: {common: ''}}])
  const [filterPhrase, setFilter] = useState('')

  const handleFilterChange = (event) => setFilter(event.target.value)

  useEffect(() => {
    if (dbug) console.log('haetaan dataa osoitteesta', dataHost)
    axios.get(dataHost)
          .then(response => {
            if (dbug) console.log('löydettiin:', response.data)
            setCountries(response.data)
          })
  }, [])

  return (
    <div>
      <Filter filterPhrase={filterPhrase}
        handleFilterChange={handleFilterChange}/>
      <RenderCountries countries={countries} filterPhrase={filterPhrase}/>
    </div>
  );
}

export default App;
